{-# OPTIONS_GHC -Wno-unrecognised-pragmas #-}
{-# HLINT ignore "Eta reduce" #-}
{-# LANGUAGE LambdaCase #-}
module Day17 where


import Data.Word
import Data.Bits
import Data.Foldable (foldl')
type Row = Word8


overlaps :: Row -> Row -> Bool
overlaps a b = a .&. b /= 0

toString :: Row -> String
toString row = [ if testBit row i then '#' else '.' | i <- [6,5..0]]


fromBools :: [Bool] -> Row
fromBools = foldl (\acc b -> acc `shiftL` 1 .|. if b then 1 else 0) 0

segment :: Int -> Int -> Row
segment whitespace filled = fromBools $ replicate whitespace False <> replicate filled True <> replicate (7-whitespace-filled) False
shapes :: [[Row]]
shapes = [line, cross, inverseL, longI, square]
line,cross,inverseL,longI,square :: [Row] 
line = [segment 2 4]
cross = [segment 3 1, segment 2 3, segment 3 1]
inverseL = [segment 4 1, segment 4 1, segment 2 3]
longI = [segment 2 1, segment 2 1, segment 2 1, segment 2 1]
square = [segment 2 2, segment 2 2]

showRows :: [Row] -> String
showRows = unlines . map toString
printRows :: [Row] -> IO ()
printRows = putStrLn . showRows

type Grid = [Row]
data Direction = L | R deriving Show

tryPlace :: [Row] -> Int -> [Row] -> Maybe [Row]
tryPlace grid off new = do
    with <- traverse step (zip within gridWithin)
    pure $ with <> gridBelow
  where
    within = drop (length new - off) $ replicate (off-length new) 0 <> new
    (gridWithin, gridBelow) = splitAt off grid
    step (a,b)
      | overlaps a b = Nothing
      | otherwise = Just (a .|. b)


shiftRow :: Direction -> [Row] -> Int -> [Row] -> Maybe (Grid, [Row])
shiftRow dir grid off new
  | hitsWall =  Nothing
  | otherwise = case tryPlace grid off new' of
     Just o -> Just (o, new')
     Nothing -> Nothing
  where
    hitsWall = case dir of
       L -> any (`testBit` 6) new
       R -> any (`testBit` 0) new
    new' = case dir of
      L -> map (`shiftL` 1) new
      R -> map (`shiftR` 1) new

doPlace :: [Direction] -> [Row] -> [Row] -> ([Direction], [Row])
doPlace dirs0 grid0 new0 = moveNew undefined dirs0 (0:0:0:0:grid0) 1 new0
  where
    moveNew old (x:dirs) grid off !new = case shiftRow x grid off new of
      Just (old', new') -> dropNew old' dirs grid off new'
      Nothing -> dropNew old dirs grid off new
    moveNew _ _ _ _ _ = error ""
    dropNew old dirs grid off !new = case tryPlace grid off' new of
      Nothing -> (dirs, dropWhile (==0) old)
      Just old' -> moveNew old' dirs grid off' new
      where off' = off+1


prefix :: Int
prefix = 2000
cycleStacks :: Int
cycleStacks = 2781
cycleLength :: Int
cycleLength = 1735
goal :: Int
goal = 1000000000000

part2 :: IO ()
part2 = print out
  where
    out = prefixAmt + cycleStacks * timesCycle + leftoverAmt
    prefixAmt = length $ stepper prefix
    leftoverAmt = length (stepper (prefix+leftoverLen)) - prefixAmt
    (timesCycle, leftoverLen) = divMod (goal - prefix) cycleLength

search :: (Int, Int)
search = head [(i, x) | i <- [1730,1731..], let x = sizeFor i, x >= cycleLength]
  where
    prefixAmt = length $ stepper prefix
    sizeFor i= length (stepper (prefix+i)) - prefixAmt

parseDirs :: String -> [Direction]
parseDirs = map $ \case
   '>' -> R
   '<' -> L
   c -> error $ "Unknown direction: " <> [c]

test :: ([Direction], [Row]) -> [Row] -> ([Direction], [Row])
test = uncurry doPlace

puts :: [Row] -> ([Direction], [Row]) -> ([Direction], [Row])
puts = flip $ uncurry doPlace

stepper :: Int -> [Row]
stepper i = snd $ foldl' (uncurry doPlace) (drop 4 testDirs, line) (take i $ drop 1 $ cycle shapes)

main :: IO ()
main = do
  print $ length $ stepper 2022
  part2
-- testDirs = cycle $ parseDirs">>><<><>><<<>><>>><<<>>><<<><<<>><>><<>>"
testDirs :: [Direction]
testDirs = cycle $ parseDirs "><<><<<>><<<<><<<>>>><<<<>><<>><>>>><<>>><><<<<>>>><>>><>>>><<<<>><<<>>><<<><<>><<>>><><<><<<>><<<<>><<<<>>>><<<<><<>>><<<><<>>><><>>>><>>><>><>>><<>><<<>><<<>>><<<<>>>><<<<>>><<>>><<>><<<>>><<>><<>>><<<>><><>><<><>>><<>>><<>><<<>>><<<>>><<<>>>><<>>><<<<>>><><<>>>><>>><<<<>>><>>>><<<<>><<<>>>><<<>><<<>>>><<>>><><<<>>><<<<>>>><<<><<<<>><<<<><<<<>>><<<>>>><>><>>><>><<<><><<<><<>><<<<>>>><<><<<<>>>><<<><>>><>><<><<<<>><<>>><<<>>>><<><<>><<<>><><>>><<<>>>><>>><<<<><<<<><<<<>>><<<<>>>><<<>>>><<<<>>><<<>><<>>><>><<<<>>>><<>>><<>>>><<<><<<>><>><<<<>><><>><>>>><<<>>><<<<>>>><<<<>>><<<<><<>>>><<<>>>><<<<>><<<>><>>>><<<>>><<<<>>>><>><<>><<<<>>><<<>>><<<>>>><><<>>>><<<<>><<>><<>>>><<>>><<>>><<<>>>><>>><<<>>><<<<>><>>>><<<<>>>><<<<>>><<<>>><<<>>><<<<><<<>>><><<<<>>><>>><<<<>>>><<<<>><>><<<<>><>>>><<<<>><<><<<><<><<<<><>>>><<>><><<<<><<<<>><>><<>>>><<<<>><>>><>><>><>>><>><<><<<><<<>>>><><>>>><<<>>><<<<>>><>>>><<>><>>><>>>><><<<><<<<>><<<><<<<>>>><<>>><<<><<<>>><<<>>><<<<>><>>>><<<<><<>>><<<><<<<>>>><<<><<<><>>><>><<<><<<>>><<<>>><<<>>>><<<<>><<<>><<<<>>>><<<<><<<><>><<>><>>><<>>>><>><<<>>><<<<>><<<<><>>>><>>><<<><<<<>>>><<>>>><<<>><>>>><<><<<<>>><<>>><<<<><>>>><<>><<><<>><><>><<<<>><<<<>>><<>><<>><>>><>>><<><<<<>><<<>>><>><<>>><<<>>>><<<>><<<<>>>><<<<>>><>>>><<>><><<<>>><<<><<<<><<>>>><<<>><<>>>><<<>>><<<>>><<<<>>>><<<>><<<>><<<>>><<<<>><>>><<>>>><<>>><<<<>>>><<<<>><<>>>><<>>><<><>>>><>><<<<><>>>><>>>><<<>>><><<<<>><>>><>>><<>>>><><><>>><<><<<>>>><<<>><<<<>>><<<<>>>><<<<><<<<>>>><<<>>>><<<<>>>><<<><<><<><<<<><>>><<><<<>><<>><<<>><>>><<<<>>><<<<>>><<<<>><>><><<<<>><<<><<>><><<>>>><>>><<<>>><<<<><<>>>><><<<>>><<>>>><<<<>><<<<>>><<<<>>><<<>>>><>><<>>>><<<><<<<><>><<<><<<<>>><<<><<<>>><<<>>><>>>><<<><>><<<><<><<<>>><<<>><<><<<<><<<>><>>><<<<>>>><>>><<<<>><<<<>>><<><<><<<<>>>><><>>>><<<<><<>>><<>><<<>>>><<>>>><<<>>>><<<><>>><<<<>>>><>>>><<<>>>><<>>>><<<<><<>><>><<>><<<>>><>>><>>><<<>><<<><<>><<>>>><<>>>><<<>>>><>>><>><<<>>>><<>>>><<<><<>>><<><<<>>><>><<<